        private Microsoft.Extensions.Logging.ILogger logger = null;

        public vpx_decoder_unittest(Xunit.Abstractions.ITestOutputHelper output)
        {
            logger = TestLogger.GetLogger(output).CreateLogger(this.GetType().Name);
